import styled from "styled-components";

const FirstContainer = styled.div`
  padding: 4rem 2rem 2rem;
  background: linear-gradient(#4d4d4d, #e66465, #121212);
`;
const FirstTitle = styled.h1`
  margin-left: 1rem;
  font-size: 2.6rem;
`;
const FirstContainerItems = styled.iframe`
  width: 30%;
  height: 80px;
  border: 0px;
  margin: 1rem 1rem 0.1rem;
`;

const SecondContainer = styled.div`
  padding: 2rem;
`;
const SecondTitle = styled.h1`
  margin-left: 1rem;
  font-size: 2rem;
`;
const SecondContainerItems = styled.iframe`
  width: 30%;
  height: 152px;
  border: 0px;
  margin: 1rem 1rem 0.1rem;
`;

export {
  FirstContainer,
  FirstTitle,
  FirstContainerItems,
  SecondContainer,
  SecondTitle,
  SecondContainerItems,
};
